return items.map(item => {
  let t = (item.json.title ?? "").toString();

  // Normalize whitespace
  t = t.replace(/[\r\n\t]+/g, " ").replace(/\s+/g, " ").trim();

  // Remove URLs (http, https, or protocol-relative //images...)
  t = t.replace(/(?:https?:)?\/\/[^\s)\]}>,]+/gi, " ").trim();

  // Remove "Trotineta Freestyle"
  t = t.replace(/\bTrotinet[ăa]\s*Freestyle\b/gi, " ").trim();

  // Remove prices
  t = t.replace(/(?:€|£|\$)\s*\d+(?:[.,]\d{2})?|\b\d{1,3}(?:[.,]\d{3})*(?:[.,]\d{2})\s*(?:€|£|\$|lei|ron)?/gi, " ").trim();

  // Remove stray symbols (keep letters, numbers, hyphens, spaces)
  t = t.replace(/[^\p{L}\p{N}\- ]+/gu, " ").trim();

  // Collapse spaces
  t = t.replace(/\s{2,}/g, " ").trim();

  // Collapse duplicated full phrase
  const dup = t.match(/^(.+?)\s+\1$/i);
  if (dup) t = dup[1];

  // --- Reorder words: move last word to the front ---
  const words = t.split(/\s+/);
  if (words.length > 1) {
    const last = words.pop();
    words.unshift(last);
  }
  const rearranged = words.join(" ");

  return { json: { title: rearranged } };
});

// Input example (items[0].json.title):
// "North Willow G5 Deck Trotinetă Freestyle\n[//images.centrano.com/...]\n94,98 €\n189,95 €\n"

const BRANDS = [
  "Academy","Alien Workshop","Apex","Blueprint BMXFIX","BSD","Cadillac Wheels","Cadillac",
  "Colony","Core","Crisp","DB Skimboards","Dial 911","Division","Dominator","Doomed",
  "Drone Scooters","Eclat","Eight Ball","Essentials Skateboarding","Family","Fiction BMX",
  "Figz Collection","Flexsurfing","Flypaper","Fuse","GoZone Skimboards","Graw Jump Ramps",
  "Grit","Habitat Skateboards","HangUp","Heart Supply","Hella Grip","Hohing","Indo",
  "JD Bug","Jessup","KFD","Kitefix","Longway","Lucky","Madrid","Mafia","Meow Skateboards",
  "Native","North Scooters","Ocean Pacific","Panda","Pivot","Prime8","Primus","Proto",
  "RAD Skateboards","Radio Bike Co","Rampage","Retrospec","Reversal","Revolution Supply Co",
  "River","Roces","Rocker","Root Industries","Salt","Sisu","Skatemate","Speed Demons",
  "Stolen","Striker","Supreme","Tall Order","Tempish","Tilt","TLC","Triple Eight",
  "Triple Skate Hook","Trynyty","Venom","Venor Skates","Verb","Wethepeople","Whitespace",
  "Wildcat","Zoo York"
];

// Escape for regex and make a pattern that tolerates flexible spacing between words.
function brandToRegex(brand) {
  const escaped = brand.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
  return new RegExp(`\\b${escaped.replace(/\s+/g, "\\s+")}\\b`, "i");
}

function findVendor(title) {
  const t = (title ?? "").toString().replace(/\s+/g, " ").trim();

  // 1) Try exact (flex-space) match, preferring the longest brand first
  const brandsByLen = [...BRANDS].sort((a, b) => b.length - a.length);
  for (const b of brandsByLen) {
    if (brandToRegex(b).test(t)) return b;
  }

  // 2) Heuristic: if the first word of a multi-word brand appears at the start of the title,
  //    map to that brand (handles cases like "North Willow..." -> "North Scooters").
  const firstWord = t.split(/\s+/)[0];
  for (const b of brandsByLen) {
    const bw = b.split(/\s+/)[0];
    if (bw.toLowerCase() === firstWord.toLowerCase()) return b;
  }

  // 3) As a fallback, check if any brand's first word exists within the first 3 words.
  const top3 = t.split(/\s+/).slice(0, 3).map(s => s.toLowerCase());
  for (const b of brandsByLen) {
    const bw = b.split(/\s+/)[0].toLowerCase();
    if (top3.includes(bw)) return b;
  }

  return "";
}

return items.map(item => {
  const title = item.json.title ?? "";
  const vendor = findVendor(title);
  return {
    json: {
      vendor,
      tag: vendor
    }
  };
});

const TYPES = [
  "Adaptor","Bar End","BPM","Casca","Ceara","Clamp","Complete","Deck","Deck End","Distantieri",
  "Frana","Furca","Genunchiere","Ghidon","Glezniere","Griptape","Headset","Imbracaminte","Imbus",
  "Inbus","Kendama","Mansoane","Peg","Roti","Rulmenti","Sporting Goods","Stand","Sticker",
  "Suruburi","Talpici"
];

// Escape regex safely
function typeToRegex(type) {
  const escaped = type.replace(/[.*+?^${}()|[\]\\]/g, "\\$&");
  return new RegExp(`\\b${escaped.replace(/\s+/g, "\\s+")}\\b`, "i");
}

function findType(title) {
  const t = (title ?? "").toString().replace(/\s+/g, " ").trim();

  // 1) Exact match first (longest types first, so "Deck End" matches before "Deck")
  const typesByLen = [...TYPES].sort((a, b) => b.length - a.length);
  for (const type of typesByLen) {
    if (typeToRegex(type).test(t)) return type;
  }

  return "";
}

return items.map(item => {
  const title = item.json.title ?? "";
  const product_type = findType(title);

  return {
    json: {
      product_type
    }
  };
});

return items.map(item => {
  const title = (item.json.title ?? "").toString();

  // Match numbers followed by optional space and € symbol
  const matches = title.match(/\d+[.,]?\d*\s*€/g) || [];

  // Convert to float (remove € and replace , with .)
  const numbers = matches.map(m =>
    parseFloat(m.replace("€", "").replace(",", ".").trim())
  ).filter(n => !isNaN(n));

  let price = 0;
  if (numbers.length > 0) {
    const highest = Math.max(...numbers);
    price = Math.ceil(highest * 5); // multiply by 5 and round up
  }

  return {
    json: {
      price
    }
  };
});

// Combine all incoming items into a single item
// Later you can use {{$json.title}} safely in the Shopify node.
const merged = Object.assign({}, ...items.map(i => i.json));
return [{ json: merged }];

